sender_wallet = Wallet()
recipient_wallet1 = Wallet()
recipient_wallet2 = Wallet()
amount1 = 200
amount2 = 300
expected = (
    """{
    "senderAddress": "%s",
    "nonce": 1,
    "expirationTime": 0,
    "actionFee": 0.01,
    "actions": [
        {
            "actionType": "TransferChx",
            "actionData": {
                "recipientAddress": "%s",
                "amount": %d
            }
        },
        {
            "actionType": "TransferChx",
            "actionData": {
                "recipientAddress": "%s",
                "amount": %d
            }
        }
    ]
}"""
     % (sender_wallet.address, recipient_wallet1.address, amount1,
    recipient_wallet2.address, amount2))
tx = Tx(sender_wallet.address, 1, 0.01, 0)
tx.add_transfer_chx_action(recipient_wallet1.address, amount1)
tx.add_transfer_chx_action(recipient_wallet2.address, amount2)
actual = tx.to_json()
assert expected == actual
