"""Test operation not mocked error is returned."""
msg = (
    'An error occurred (NoSuchMethod) when calling the CreateGecko operation: Operation not mocked.'
    )
try:
    mocker = MockIAM()
    mocker.mock_make_api_call('CreateGecko', {'Name': 'gecko'})
except MockBoto3ClientError as e:
    assert msg == str(e)
