assert self.package.use_lambda_file('lambdas/dev/hello_world.py').get('status'
    ) == 'ok'
files = self.package.get_files()
assert len(files) == 1
assert Files.file_name(files.pop()) == '{0}.py'.format(self.package.lambda_name
    )
assert self.package.update().get('status') == 'ok'
assert self.package.invoke({'name': 'world'}
    ) == 'From lambda code, hello world'
